import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import Dropdown from 'react-bootstrap/Dropdown';

const LanguageSelector = ({ translations, onLanguageChange }) => {
  const [selectedLanguage, setSelectedLanguage] = useState(translations[0]); // Initialize with the first language

  const handleLanguageChange = (language) => {
    setSelectedLanguage(language);
    onLanguageChange(language); // Notify the parent component about the language change
  };

  return (
    <Dropdown>
      <Dropdown.Toggle variant="secondary" id="dropdown-basic">
        {selectedLanguage}
      </Dropdown.Toggle>
      <Dropdown.Menu style={{ maxHeight: '200px', overflowY: 'auto' }}>
        {translations.map((language, index) => (
          <Dropdown.Item
            key={index}
            onClick={() => handleLanguageChange(language)}
          >
            {language}
          </Dropdown.Item>
        ))}
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default LanguageSelector;
