import React, { useState } from 'react';

const LanguageSelector = ({ translations }) => {
  const [selectedLanguage, setSelectedLanguage] = useState('ara');

  const handleLanguageChange = (e) => {
    setSelectedLanguage(e.target.value);
  };

  return (
    <div>
      <div className="form-group">
        <label htmlFor="languageSelect">Choose a Language:</label>
        <select
          className="form-select form-select-sm"
          aria-label="Small select example"
          style={{ width: "250px" }}
          value={selectedLanguage}
          onChange={handleLanguageChange}
        >
          {Object.keys(translations).map((langCode) => (
            <option key={langCode} value={langCode}>
              {langCode}
            </option>
          ))}
        </select>
      </div>
      <div className="translation">
        <li>Official: {translations[selectedLanguage].official}</li>
        <li>Common: {translations[selectedLanguage].common}</li>
      </div>
    </div>
  );
};

export default LanguageSelector;
