import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import CountryInfo from './CountryInfo';

function App() {
  const [countryName, setCountryName] = useState('');
  const [countryData, setCountryData] = useState(null);

  const fetchCountryData = async () => {
    try {
      const response = await axios.get(`https://restcountries.com/v3.1/${countryName}`);
      setCountryData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="App">
      <div className="container mt-4">
        <h1 className="mb-4">Country Information App</h1>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Enter a country name"
            value={countryName}
            onChange={(e) => setCountryName(e.target.value)}
          />
          <button className="btn btn-primary" onClick={fetchCountryData}>
            Get Info
          </button>
        </div>
      </div>
      {countryData && <CountryInfo countryData={countryData} />}
    </div>
  );
}

export default App;
