import React, { useState } from 'react';
import CountryDetails from './CountryDetails';

const renderNestedJson = (json) => {
    return JSON.stringify(json, null, 2);
    };

const CountryItem = ({ country }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleCollapse = () => {
    setIsOpen(!isOpen);
  };

const { name, capital, population, currencies, languages, flag } = country;


  return (
    <div className="country-item">
      <button
        className="btn btn-outline-dark btn-lg"
        onClick={toggleCollapse}
      >
        {country.name.common + ' ' + flag}
      </button>
      {isOpen && (
        <div className="country-details">
            <div className="card-body">
            <p className="card-text">
            <strong>Capital:</strong> {capital}
            </p>
            <p className="card-text">
            <strong>Population:</strong> {population}
            </p>
            <p className="card-text">
            <strong>Currencies:</strong> {renderNestedJson(currencies)}
            </p>
            <p className="card-text">
            <strong>Languages:</strong> {renderNestedJson(languages)}
            </p>
            <p>-------</p>
            {console.log(country)}



        </div>


        </div>
      )}
    </div>
  );
};

export default CountryItem;
