import React, { useState } from 'react';
import CountryDetails from './CountryDetails';
import JsonDisplay from './JsonDisplay';

const renderNestedJson = (json) => {
    return JSON.stringify(json, null, 2);
    };

const CountryItem = ({ country }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleCollapse = () => {
    setIsOpen(!isOpen);
  };

const { name, capital, population, currencies, languages, flag } = country;


  return (
    <div class="col-8" className="country-item">
    <div data-bs-spy="scroll" data-bs-target="#simple-list-example" data-bs-offset="0" data-bs-smooth-scroll="true" class="scrollspy-example" tabindex="0">

      <button
        className="btn btn-outline-dark btn-lg"
        onClick={toggleCollapse}
      >
        {country.name.common + ' ' + flag}
      </button>
      {isOpen && (
        <div className="country-details">
            <div className="card-body">
            <p className="card-text">
            <strong>Capital:</strong> {capital}
            </p>
            <p className="card-text">
            <strong>Population:</strong> {population}
            </p>
            <p className="card-text">
            <strong>Currencies:</strong> 
            <JsonDisplay data={currencies} />
            </p>
            <p className="card-text">
            <strong>Languages:</strong> 
            <JsonDisplay data={languages} />
            </p>
            <p>-------</p>
            {console.log(country)}
            <CountryDetails countryData={country} />


        </div>


        </div>
      )}
    </div>
    </div>
  );
};

export default CountryItem;
