import React, { useState } from 'react';
import CountryItem from './CountryItem';

const CountryList = ({ countryData, currentPage, itemsPerPage  }) => {
  const [currentCountries, setCurrentCountries] = useState([]);

  const indexOfLastCountry = currentPage * itemsPerPage;
  const indexOfFirstCountry = indexOfLastCountry - itemsPerPage;


  if(Object.keys(countryData).length < indexOfLastCountry){
    setCurrentCountries(Object.values(countryData).slice(indexOfFirstCountry, Object.keys(countryData).length));
  } else {
    setCurrentCountries(Object.values(countryData).slice(indexOfFirstCountry, indexOfLastCountry));
  }

  return (
    <div className="container mt-4">
      <h3 className="mb-4">Country Information</h3>
      <table className="table table-bordered">

        <tbody>
          {/* {Object.entries(countryData).map(([property, value]) => (
            <tr key={property}>
              <td>{parseInt(property)+1}</td>
              <td>

                {value && <CountryItem country={value} />}
                </td>
            </tr>
          ))} */}

          {currentCountries.map((country, index) => (
            <tr key={index}>
              <td>{index+1}</td>
              <td> <CountryItem country={country} /></td>
            </tr>
          ))}

        </tbody>
      </table>
    </div>
  );
};

export default CountryList;
